import React from 'react';
import EmployeeCard from './components/EmployeeCard';
import Wrapper from './components/Wrapper';
import Title from './components/Title';
import employees from './employees.json';
import SortButtons from './components/SortButtons'


class App extends React.Component {
  state = {
    employees: employees
  };

  sortId = event => {
    event.preventDefault();
    // console.log("sortId reached")

    let newArray = testSort.sort((a, b) => (a.id > b.id) ? 1 : -1)
    // console.log(newArray)
    this.setState({
      employees: newArray
    });
  }

  sortName = event => {
    event.preventDefault();
    // console.log("sortName reached")

    let newArray = testSort.sort((a, b) => (a.name > b.name) ? 1 : -1)
    // console.log(newArray)
    this.setState({
      employees: newArray
    });
  }

  sortDob = event => {
    event.preventDefault();
    // console.log("sortDob reached")

    let newArray = testSort.sort((a, b) => (a.dob > b.dob) ? 1 : -1)
    // console.log(newArray)
    this.setState({
      employees: newArray
    });
  }


  render() {
    return (
      <Wrapper>
        <Title>Employee Directory</Title>
        <SortButtons
          sortId={this.sortId}
          sortName={this.sortName}
          sortDob={this.sortDob}
        />
        {this.state.employees.map(item => {
          return (
            <EmployeeCard
              name={item.name}
              id={item.id}
              key={item.id}
              image={item.image}
              phone={item.phone}
              email={item.email}
              dob={item.dob}
              sortID={this.sortID}
            />
          )
        })}
      </Wrapper>
    );
  }
}

export default App;
